import urlparse,urllib,re

try:
    from core import logger
    from core import scrapertools
    from core.item import Item
except:
    # En Plex Media server lo anterior no funciona...
    from Code.core import logger
    from Code.core import scrapertools
    from Code.core.item import Item

logger.info("[rtvc.py] init")

DEBUG = False
CHANNELNAME = "rtvc"

def isGeneric():
    return True

def mainlist(item):
    logger.info("[rtvc.py] mainlist")
    itemlist=[]

    # Empieza con el listado s√≥lo de videos
    url="http://www.rtvc.es/television/emisiones.aspx"
    data="ctl00$content$ScriptManager1=ctl00$content$ScriptManager1|ctl00$content$imgVideos&__EVENTTARGET=&__EVENTARGUMENT=&__LASTFOCUS=&__VIEWSTATE=%%3D&ctl00$loginbuttons$txtUsuario=&ctl00$loginbuttons$txtPassword=&ctl00$content$ddlEmisionesCategoria=0&ctl00$content$typeselected=todo&ctl00$content$imgVideos.x=49&ctl00$content$imgVideos.y=9"
    
    # Descarga la p√°gina
    data = scrapertools.cache_page(url,post=data)
    #logger.info(data)

    # Extrae las categor√≠as de v√≠deos
    patron  = '<select name="ctl00$content$ddlEmisionesCategoria"[^>]+>(.*?)</select>'
    matches = re.compile(patron,re.DOTALL).findall(data)
    if len(matches)>0:
        data = matches[0]

    patron  = '<option value="([^"]+)">([^<]+)</option>'
    matches = re.compile(patron,re.DOTALL).findall(data)

    for match in matches:
        # Atributos del v√≠deo
        scrapedtitle = match[1].strip()
        scrapedurl = match[0]
        scrapedthumbnail = ""
        scrapedplot = ""
        if (DEBUG): logger.info("title=["+scrapedtitle+"], url=["+scrapedurl+"], thumbnail=["+scrapedthumbnail+"]")

        # A√±ade al listado de XBMC
        itemlist.append( Item(channel=CHANNELNAME, title=scrapedtitle , action="programlist" , url=scrapedurl, thumbnail=scrapedthumbnail, plot=scrapedplot , show="" , category = scrapedtitle , folder=True) )

    return itemlist

def programlist(item):
    logger.info("[rtvc.py] programlist")
    itemlist=[]

    # La url es el id del combo de categor√≠as, lo pega dentro del DATA del POST
    data="ctl00$content$ScriptManager1=ctl00$content$UpdatePanel1|ctl00$content$ddlEmisionesCategoria&__EVENTTARGET=ctl00%24content%24ddlEmisionesCategoria&__EVENTARGUMENT=&__LASTFOCUS=&__VIEWSTATE=%%3D%3D&ctl00$loginbuttons$txtUsuario=&ctl00$loginbuttons$txtPassword=&ctl00$content$ddlEmisionesCategoria="+item.url+"&ctl00$content$typeselected=video&"
    url="http://www.rtvc.es/television/emisiones.aspx"
    
    # Descarga la p√°gina
    data = scrapertools.cache_page(url,post=data)
    #logger.info(data)

    # Extrae los programas
    patron  = '<div class="emision">[^<]+'
    patron += '<div class="photo">[^<]+'
    patron += '<img id="[^"]+" src="/handlers/ThumbnailHandler.ashx\?width=90\&amp\;height=60\&amp\;fill=False\&amp\;src=([^"]+)"[^>]+>[^<]+'
    patron += '</div>[^<]+'
    patron += '<div class="data">[^<]+'
    patron += '<a class="title" href="([^"]+)">[^<]+'
    patron += '<span id="title" title="([^"]+)">[^<]+</span>'
    matches = re.compile(patron,re.DOTALL).findall(data)

    for match in matches:
        # Atributos del v√≠deo
        scrapedtitle = match[2].strip()
        scrapedurl = urlparse.urljoin(url,match[1])
        # /handlers/ThumbnailHandler.ashx?width=90&amp;height=60&amp;fill=False&amp;src=
        #scrapedthumbnail = urlparse.urljoin(url,match[0].replace("&amp;","&"))
        scrapedthumbnail = urlparse.urljoin(url,match[0].replace(" ","%20"))
        scrapedplot = ""
        if (DEBUG): logger.info("title=["+scrapedtitle+"], url=["+scrapedurl+"], thumbnail=["+scrapedthumbnail+"]")

        # A√±ade al listado de XBMC
        itemlist.append( Item(channel=CHANNELNAME, title=scrapedtitle , action="videolist" , url=scrapedurl, page=scrapedurl , thumbnail=scrapedthumbnail, plot=scrapedplot , show=scrapedtitle , category = item.category , folder=True) )

    return itemlist

def videolist(item):
    logger.info("[rtvc.py] videolist")
    itemlist=[]

    # Descarga la p√°gina
    data = scrapertools.cachePage(item.url)
    #logger.info(data)

    # Extrae la tabla de los v√≠deos
    patron  = '<table id="emisiones"[^>]+>.*?<tbody>(.*?)</tbody>'
    matches = re.compile(patron,re.DOTALL).findall(data)
    
    if len(matches)>0:
        data = matches[0]
    
    '''
    <tr>
    <td>1126_030910-BDC.wmv</td>
    <td>Video</td>
    <td>03/09/2010</td>
    <td>Interceptado un barco negrero en aguas de Gran Canaria. La tripulaci√≥n ha sido detenida.</td>
    <td class="play"><a id="ctl00_content_repEmisiones_ctl01_lnkAction" href="javascript:PopupCenter('/television/diferido.aspx?id=1126&amp;fichero=1126_030910-BDC.wmv','Diferido','745','548')"><img src="/images/web/btn_emisiones_play.png" style="border-width:0px;" /></a></td>
    <td class="download"></td>
    </tr>
    '''

    patron  = '<tr>[^<]+'
    patron += '<td>[^<]+</td>[^<]+'
    patron += '<td>Video</td>[^<]+'
    patron += '<td>([^<]+)</td>[^<]+'
    patron += '<td>([^<]+)</td>[^<]+'
    patron += '<td class="play"><a id="[^"]+" href="javascript\:PopupCenter\(\'([^\']+)\'[^"]+"><img[^>]+></a></td>[^<]+'
    patron += '<td class="download">'
    matches = re.compile(patron,re.DOTALL).findall(data)

    for match in matches:
        # Atributos del v√≠deo
        scrapedtitle = item.title+" ("+match[0].strip()+")"
        scrapedurl = urlparse.urljoin(item.url,match[2])
        scrapedthumbnail = ""
        scrapedplot = match[1].strip()
        if (DEBUG): logger.info("title=["+scrapedtitle+"], url=["+scrapedurl+"], thumbnail=["+scrapedthumbnail+"]")

        # A√±ade al listado de XBMC
        itemlist.append( Item(channel=CHANNELNAME, title=scrapedtitle , action="getvideo" , url=scrapedurl, thumbnail=scrapedthumbnail, plot=scrapedplot , show=item.show , category = item.category , folder=True) )

    return itemlist

def getvideo(item):
    logger.info("[rtvc.py] play")

    # Descarga la p√°gina
    url = item.url.replace("&amp;","&")
    logger.info("[rtvc.py] url="+url)
    data = scrapertools.cache_page(url)
    patron  = '<input type="hidden" id="hidden_url" value=\'([^\']+)\''
    matches = re.compile(patron,re.DOTALL).findall(data)
    url = matches[0]
    logger.info("[rtvc.py] url="+url)

    itemlist = []
    itemlist.append( Item(channel=CHANNELNAME, title=item.title , action="play" , url=url, thumbnail=item.thumbnail , plot=item.plot , server = "directo" , show = item.show , category = item.category , folder=False) )

    return itemlist